	function ajaxCallRemoteDeleteNotif(portletContext, idNotif, pIndexNotif, pTheme, pStatut, pTitre) {
		
		var url = '&' + portletContext + 'idNotif=' + idNotif + '&idNoCache=' + new Date().getTime();
		var functionActionUrl = '/PortailAS/redirection.do?redirect=ACC_NOTIF_SUPP&_trackArgs[2]=' + pTheme + '&_trackArgs[3]=' + pStatut + '&_trackArgs[9]=' + pTitre + url;
		
		updateDisplayDeletedNotif(idNotif);	
		
		if (window.XMLHttpRequest)
		{	// Non-IE browsers
			req = new XMLHttpRequest();
			req.onreadystatechange = ajaxReturn;
			req.open('GET', functionActionUrl, true);
			req.send(null);
		}
		else if (window.ActiveXObject) 
		{	// IE
			req = new ActiveXObject('Microsoft.XMLHTTP');
			req.onreadystatechange = ajaxReturn;
			req.open('GET', functionActionUrl, true);
			req.send();
		}
		else {
			return; // Navigateur non compatible
		}
	};
	
	function ajaxReturn(){
		return function() {
			if (req.readyState == 4)
			{ 	// Complete
				if (req.status == 200)
				{ 	// OK response
				}
			}
		}
	};

	
	function updateDisplayDeletedNotif(pIdNotif){		 
		// Si la notification est non lue on met a jour l'affichage du titre
		updateNotificationTitle();
		// S'il n'y a plus de notifs, cacher l'encart des notifs
		document.getElementById('nbreNotifCache').innerHTML -= 1;

		// Suppression de l'affichage de la notification
		document.getElementById('tableauNotifications').removeChild(document.getElementById('notif_' + pIdNotif));	
	};
	
	
	function consommerNotif(idNotif, redirection, isInterneLink, urlRedirection) {
	
		var functionActionUrl = '/PortailAS/ajax.do?_nfpb=true&notificationsAccueilidNotif=' + idNotif + '&notificationsAccueilactionEvt=consommerNotification&idNoCache=' + new Date().getTime();
		
		updateDisplayConsumedNotif(idNotif);
		
		// Call ajax
		if (window.XMLHttpRequest)
		{	// Non-IE browsers
			req = new XMLHttpRequest();
			req.onreadystatechange = function() {
				
				if (urlRedirection && urlRedirection != '') {
					if (req.readyState == 4)
					{ 	// Complete
						if (req.status == 200)
						{ 	// OK response
							// Call ajax
							tracerNotif(urlRedirection, redirection, isInterneLink);
						}
					}
						
				} else {
					doActualRedirect(req, redirection, isInterneLink);
				}
			};
			req.open('GET', functionActionUrl, true);
			req.send(null);
		}
		else if (window.ActiveXObject) 
		{	// IE
			req = new ActiveXObject('Microsoft.XMLHTTP');
			req.onreadystatechange = function() {
				
				if (urlRedirection && urlRedirection != '') {
					if (req.readyState == 4)
					{ 	// Complete
						if (req.status == 200)
						{ 	// OK response
							// Call ajax
							tracerNotif(urlRedirection, redirection, isInterneLink);
						}
					}
				} else {
					doActualRedirect(req, redirection, isInterneLink);
				}
			};
			req.open('GET', functionActionUrl, true);
			req.send();
		}
		else {
			return false; // Navigateur non compatible
		}
		return false;
	};
	
	
	function tracerNotif(urlRedirection, doTrace, isInterneLink) {
		
		if (window.XMLHttpRequest)
		{	// Non-IE browsers
			request = new XMLHttpRequest();
			request.onreadystatechange = function() {doActualRedirect(request, urlRedirection, isInterneLink);};
			request.open('GET', doTrace, true);
			request.send(null);
		}
		else if (window.ActiveXObject) 
		{	// IE
			request = new ActiveXObject('Microsoft.XMLHTTP');
			request.onreadystatechange = function() {doActualRedirect(request, urlRedirection, isInterneLink);};
			request.open('GET', doTrace, true);
			request.send();
		}
		else {
			return false; // Navigateur non compatible
		}
		return false;
	}
	
	
	
	function doActualRedirect(currentRequest, redirection, isInterneLink) {
		if (currentRequest.readyState == 4)
		{ 	// Complete
			if (currentRequest.status == 200)
			{ 	// OK response
				if(redirection != ''){
					if(isInterneLink == true || isInterneLink == "true"){
						window.location = redirection;
					} else {
						window.open(redirection);
					}
				}
			}
		}
	};
	
	
	function updateDisplayConsumedNotif(pIdNotif){
		
		// Afficher la notification comme consommée
		document.getElementById('notif_msglabel_' + pIdNotif).className='msglabel';
		document.getElementById('notif_msglabel_' + pIdNotif).setAttribute("onclick", null);
	};
